import React, { Component } from 'react';
import './customer.css';

class Customer extends Component {
  submit(){
  var input = document.getElementById('addInput')
  }
  constructor(){
    super();
    this.state = {
      Results:[]
  }
}

componentDidMount(){
  fetch('/api/customers',{
  method: "POST",
   body:  JSON.stringify(input)
  }
  )
  .then(res => res.json())
  .then(Results => this.setState({Results}, () => console.log('results fetched',Results)))
}
  render() {    
    return (
   
  <div className="Search"> 
      <input onChange={this.inputvalue}
          type="text"
          className="input"
          id="addInput"
          placeholder="Enter Chemical Composition"
        />
         <button className="button is-info" onClick={this.submit}>
    Submit 
  </button>
  <input type="checkbox" className= "boxinput" id="Exact" value="Composition" /> Exact Composition only
    </div>

    

  

    );
  }
}

export default Customer;
