import './customer.css';

class Customer extends Component {
    
   
    submit(){
        var x = document.getElementById("elevalu").value;
        var y = document.getElementById("Exact").checked;
        console.log("checkbox vlaue ",y);
        console.log("without split",x);
    }
  render() {    
    return (
      <div className="Search"> 
      <input
          type="text"
          className="input"
          id="addInput"
          placeholder="Enter Chemical Composition"
        />
         <button className="button is-info" onClick={this.submit}>
    Submit 
  </button>
    </div>

  

    );
  }
}

export default Customer;
