import React, { Component } from 'react';
import './customer.css';
import Request from 'superagent';

class Customer extends Component {
  constructor(){
    super();
    this.state = {};
}
componentDidMount(){
  this.Search();
}
handleSubmit(e){
    console.log("find the input data");
    var x = this.refs.addInput.value;
    var y = this.refs.Exact.checked;
    var inpsp = x.split(";");
    console.log(inpsp,y)
    var inpuele =[];
    var inpunele =[];
    for(var f=0;f<inpsp.length;f++){
      var val  =inpsp[f].replace(/[A-Z]/g,'').replace(/\s+/g,'').replace(/[a-z]/g,'');
      var inte = inpsp[f].replace(/[0-9.]/g,'').replace(/\s+/g, '');
      inpunele.push(parseFloat(val)*100);
      inpuele.push(inte);
      var lengd = inpuele.length;
      // this.Search(inpuele,inpunele,lengd)
      this.Search();
    }
  e.preventDefault();
}

  render() {    
    return (
      <form onSubmit ={this.handleSubmit.bind(this)}>
  <div className="Search"> 
      <input 
          type="text"
          className="input"
          ref="addInput"
          placeholder="Enter Chemical Composition"
        />
         <button className="button is-info">
    Submit 
  </button>
  <input type="checkbox" className= "boxinput" ref="Exact" value="Composition" /> Exact Composition only
    </div>
    </form>
    );

  }
  Search(){
  // var url = "/api/customers";
  // Request.get(url).send({hello: 'hello w'}).set('Accept','data').then((response) => {
  //   this.setState({
  //     Ress: response.body
  //   })
  //   console.log("insisde the response",response.body);
  // });
  Request
.get('/api/customers')
.set('Content-Type', 'application/x-www-form-urlencoded')
.send({ username: "username", password: "password" })
.end(function(err, res){
console.log(res.body);
}); 

  }
}


export default Customer;
