    componentDidMount() {
        this.$el = $(this.el);
        this.$el.DataTable({
           data: this.props.datta,
           columns: [
                {title: 'id'},
                {ti: "Elements"},
                {data: "numbersel"},
                { data: 'Energy' },
                { data: 'Formation' },
                {data:"forunit"}
            ],
        });
    }  
    componentWillUnmount(){
    //    $('.data-table-wrapper')
