import React, { Component } from 'react';
import './customer.css';

class Customer extends Component {
    constructor(){
        super();
        this.state ={
            customers:[]
        }
    }
    componentDidMount(){
        fetch('/api/customers')
        .then(res => res.json())
        .then(customers => this.setState({customers} , () => console.log('customer fetched',customers)));
    }
  render() {    
    return (
 <div>
 
</div>
    );
  }
}

export default Customer;
